import dbg
import math
import time

def old_testing_kmer_size(minK: int, maxK: int, reads: list):
    """testing kmer size in range [minK, minK]""" 
    start = time.time()

    print('First test')
    max_length = -math.inf
    for i in range(minK, maxK+1, 5):
        contigs, vertices, edges, k = dbg.assembly(reads, i)
        print(f"K: {i} length: {len(contigs)}")
        if len(contigs) > max_length:
            max_length = len(contigs)
            bestK = i
            Contigs = contigs 
            Vertices = vertices 
            Edges = edges
            

    minK = max(minK, bestK-5)
    maxK = min(maxK, bestK+5)

    print('Second test')
    for i in range(minK, maxK):
        if i == bestK:
            continue
        contigs, vertices, edges, k = dbg.assembly(reads, k=i)
        print(f"K: {i} length: {len(contigs)}")
        if len(contigs) > max_length:
            max_length = len(contigs)
            Contigs = contigs 
            Vertices = vertices 
            Edges = edges
            bestK = i

    print(f'Time: {time.time() - start}s')
    print(f"Best Kmer size: {bestK}")

    return Contigs, Vertices, Edges

def st_read_fasta(fasta):
    reads = []
    i = 0
    seq = ""
    for line in fasta.readlines():
        if not line.startswith('+') and not line.startswith('@') and not line.startswith('#') and not line.startswith('>'):
            seq += line[:-1]
        else:
            reads.append(seq)
            i += 1
            seq = ""
            if i >= 100:
                break
    
    reads.pop(0)

    return reads

